mapper.select:
SELECT 

FROM business_result_item a

WHERE a.id = #{id}

SELECT 

FROM business_result_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.assemble_id = #{assembleId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM business_result_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE business_result_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
