mapper.select:
SELECT 

FROM car_motor_cycle a

WHERE a.id = #{id}

SELECT 

FROM car_motor_cycle a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.work_work_shop_id = #{workWorkShopId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM car_motor_cycle a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE car_motor_cycle SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
