mapper.select:
SELECT 

FROM car_wagon a

WHERE a.id = #{id}

SELECT 

FROM car_wagon a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.car_motor_cycle_id = #{carMotorCycleId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM car_wagon a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE car_wagon SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
