mapper.select:
SELECT 

FROM check_spot_item a

WHERE a.id = #{id}

SELECT 

FROM check_spot_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.assemble_id = #{assembleId}


		AND a.item = #{item}


		AND a.part = #{part}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM check_spot_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE check_spot_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
