mapper.select:
SELECT 

FROM item_qx2b_mt_4400_dj a

WHERE a.del_flag = 0 AND a.id = #{id}

SELECT 

FROM item_qx2b_mt_4400_dj a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.part = #{part}


		AND a.number = #{number}


		AND a.check_content = #{checkContent}


		AND a.assemble_id = #{assembleId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM item_qx2b_mt_4400_dj a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

