mapper.select:
SELECT 

FROM spot_check_content a

WHERE a.id = #{id}

SELECT 

FROM spot_check_content a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scsp_id = #{scspId}


		AND a.old_new = #{oldNew}


		AND a.item = #{item}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM spot_check_content a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE spot_check_content SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
