mapper.select:
SELECT 

FROM spot_check_single_person a

WHERE a.id = #{id}

SELECT 

FROM spot_check_single_person a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scmp_id = #{scmpId}


		AND a.work_person_id = #{workPersonId}


		AND a.submit_state = #{submitState}


		AND a.item = #{item}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM spot_check_single_person a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE spot_check_single_person SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
