mapper.select:
SELECT 

FROM work_shop a

WHERE a.id = #{id}

SELECT
FROM work_shop a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM work_shop a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE work_shop SET 	
	name = #{name},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE work_shop SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
